import React, { useState, useEffect, useRef } from 'react';

const MusicExperimentApp = () => {
  // State management
  const [currentScreen, setCurrentScreen] = useState('intro'); // intro, loading, experiment, completion
  const [currentStimulusIndex, setCurrentStimulusIndex] = useState(0);
  const [experimentResults, setExperimentResults] = useState({
    participantId: '',
    demographicInfo: {},
    stimuli: [],
    experimentSummary: {}
  });

  // Demographic form states
  const [formData, setFormData] = useState({
    age: '',
    gender: '',
    musicalBackground: '',
    theory: '',
    listenFrequency: '',
    liveFrequency: '',
    listeningEnvironment: '',
    participantId: ''
  });

  // Experiment states
  const [selectedStimuli, setSelectedStimuli] = useState([]);
  const [hasPlayedStimulus, setHasPlayedStimulus] = useState(false);
  const [playCountsPerStimulus, setPlayCountsPerStimulus] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playProgress, setPlayProgress] = useState(0);

  // Response states
  const [responses, setResponses] = useState({
    likeRating: 5,
    complexRating: 4,
    familiarRating: 4,
    structureRating: '',
    emotionRating: '',
    engagementRating: 4,
    selectedComponent: '',
    genreSelection: ''
  });

  // Refs
  const audioRef = useRef(null);
  const experimentStartTimeRef = useRef(null);
  const currentStimulusStartTimeRef = useRef(null);
  const playIntervalRef = useRef(null);

  // Constants
  const totalStimuli = 5; // Reduced for demo

  // Music files data
  const musicFiles = [
    { file: "/music/classical/classical_1.wav", genre: "classical", index: 0 },
    { file: "/music/math_rock/math_rock_1.wav", genre: "math_rock", index: 6 },
    { file: "/music/pop_dance/pop_dance_1.wav", genre: "pop_dance", index: 12 },
    { file: "/music/jazz/jazz_1.wav", genre: "jazz", index: 18 },
    { file: "/music/scandipop_tropical/scandipop_tropical_1.wav", genre: "scandipop_tropical", index: 24 },
  ];

  // Utility functions
  const generateParticipantId = () => {
    const timestamp = new Date().getTime();
    const randomPart = Math.floor(Math.random() * 10000);
    return `P${timestamp}-${randomPart}`;
  };

  const shuffleArray = (array) => {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  };

  const selectMusic = () => {
    // For demo, just shuffle all available music
    return shuffleArray(musicFiles).slice(0, totalStimuli);
  };

  // Event handlers
  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleResponseChange = (field, value) => {
    setResponses({
      ...responses,
      [field]: value
    });
  };

  const startExperiment = () => {
    // Validate form
    const requiredFields = ['age', 'gender', 'musicalBackground', 'theory', 'listenFrequency', 'liveFrequency', 'listeningEnvironment'];
    const isValid = requiredFields.every(field => formData[field]);

    if (!isValid) {
      alert('開始實驗前請填寫資訊。');
      return;
    }

    // Set loading screen
    setCurrentScreen('loading');

    // Initialize experiment
    experimentStartTimeRef.current = new Date();
    const participantId = formData.participantId || generateParticipantId();

    // Select music
    const selected = selectMusic();
    setSelectedStimuli(selected);

    // Initialize play counts
    setPlayCountsPerStimulus(new Array(totalStimuli).fill(0));

    // Set experiment results
    setExperimentResults({
      participantId,
      demographicInfo: {
        ...formData,
        experimentStartTime: experimentStartTimeRef.current.toISOString()
      },
      stimuli: [],
      experimentSummary: {}
    });

    // Simulate loading
    setTimeout(() => {
      setCurrentScreen('experiment');
      currentStimulusStartTimeRef.current = new Date();
    }, 1500);
  };

  const playCurrentStimulus = () => {
    if (!selectedStimuli[currentStimulusIndex]) return;

    const stimulus = selectedStimuli[currentStimulusIndex];

    // Update play count
    const newPlayCounts = [...playCountsPerStimulus];
    newPlayCounts[currentStimulusIndex]++;
    setPlayCountsPerStimulus(newPlayCounts);

    setIsPlaying(true);
    setPlayProgress(0);

    // Create new audio element
    const audio = new Audio(stimulus.file);
    audioRef.current = audio;

    // Set up event listeners
    audio.addEventListener('loadedmetadata', () => {
      audio.play().catch(err => {
        console.error('Play error:', err);
        alert('請點擊播放按鈕後允許音訊播放。');
      });

      const duration = audio.duration;
      const startTime = Date.now();

      // Update progress
      playIntervalRef.current = setInterval(() => {
        const elapsed = (Date.now() - startTime) / 1000;
        const progress = (elapsed / duration) * 100;

        if (audio.ended || progress >= 100) {
          clearInterval(playIntervalRef.current);
          setIsPlaying(false);
          setHasPlayedStimulus(true);
          setPlayProgress(100);
        } else {
          setPlayProgress(progress);
        }
      }, 100);
    });

    audio.addEventListener('error', (e) => {
      console.error('Audio error:', e);
      alert('無法播放音樂檔案。請確認檔案路徑是否正確。');
      setIsPlaying(false);
    });

    // Load the audio
    audio.load();
  };

  const nextStimulus = () => {
    if (!hasPlayedStimulus) {
      alert('請先聆聽音樂再繼續。');
      return;
    }

    // Validate responses
    if (!responses.structureRating || !responses.emotionRating || 
        !responses.selectedComponent || !responses.genreSelection) {
      alert('請填寫所有問題再繼續。');
      return;
    }

    // Stop current audio if playing
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current = null;
    }

    // Clear play interval
    if (playIntervalRef.current) {
      clearInterval(playIntervalRef.current);
    }

    // Record results
    const questionEndTime = new Date();
    const currentStimulus = selectedStimuli[currentStimulusIndex];

    const newStimuli = [...experimentResults.stimuli];
    newStimuli[currentStimulusIndex] = {
      stimulusIndex: currentStimulusIndex,
      fileInfo: currentStimulus,
      ...responses,
      questionStartTime: currentStimulusStartTimeRef.current.toISOString(),
      questionEndTime: questionEndTime.toISOString(),
      questionDurationMs: questionEndTime - currentStimulusStartTimeRef.current,
      totalPlayCount: playCountsPerStimulus[currentStimulusIndex]
    };

    setExperimentResults({
      ...experimentResults,
      stimuli: newStimuli
    });

    // Reset for next stimulus
    if (currentStimulusIndex + 1 >= totalStimuli) {
      completeExperiment();
    } else {
      setCurrentStimulusIndex(currentStimulusIndex + 1);
      setHasPlayedStimulus(false);
      setPlayProgress(0);
      currentStimulusStartTimeRef.current = new Date();

      // Reset responses
      setResponses({
        likeRating: 5,
        complexRating: 4,
        familiarRating: 4,
        structureRating: '',
        emotionRating: '',
        engagementRating: 4,
        selectedComponent: '',
        genreSelection: ''
      });
    }
  };

  const completeExperiment = () => {
    const experimentEndTime = new Date();
    const totalDuration = experimentEndTime - experimentStartTimeRef.current;

    // Calculate statistics
    const totalPlays = playCountsPerStimulus.reduce((sum, count) => sum + count, 0);
    const avgPlays = (totalPlays / totalStimuli).toFixed(2);

    const finalResults = {
      ...experimentResults,
      experimentSummary: {
        experimentStartTime: experimentStartTimeRef.current.toISOString(),
        experimentEndTime: experimentEndTime.toISOString(),
        totalExperimentDurationMs: totalDuration,
        totalExperimentDurationMin: (totalDuration / 60000).toFixed(2),
        playStatistics: {
          totalPlays,
          averagePlaysPerStimulus: avgPlays
        }
      }
    };

    setExperimentResults(finalResults);
    setCurrentScreen('completion');

    // Submit to server
    submitDataToFormspree(finalResults);
  };

  const submitDataToFormspree = async (data) => {
    const formData = {
      participantId: data.participantId,
      ...data.demographicInfo,
      experimentDuration: data.experimentSummary.totalExperimentDurationMin + "分鐘",
      totalPlays: data.experimentSummary.playStatistics.totalPlays,
      demographicInfo: JSON.stringify(data.demographicInfo),
      experimentSummary: JSON.stringify(data.experimentSummary)
    };

    // Add stimuli data
    data.stimuli.forEach((stimulus, index) => {
      formData[`stimulus_${index + 1}`] = JSON.stringify(stimulus);
    });

    try {
      const response = await fetch('https://formspree.io/f/xyzwvbgg', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        console.log('Data submitted successfully');
      }
    } catch (error) {
      console.error('Error submitting data:', error);
    }
  };

  const downloadResults = () => {
    const dataStr = JSON.stringify(experimentResults, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);

    const a = document.createElement('a');
    a.href = url;
    a.download = `music_experiment_${experimentResults.participantId}_${new Date().toISOString()}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Clean up audio on unmount
  useEffect(() => {
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
      if (playIntervalRef.current) {
        clearInterval(playIntervalRef.current);
      }
    };
  }, []);

  // Render functions for different screens
  const renderIntroScreen = () => (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">NTHU 2025 MIR 課程期末專案實驗</h1>

      <div className="bg-white rounded-lg shadow-lg p-8">
        <h2 className="text-2xl font-semibold mb-4">歡迎您參與本次音樂感知實驗！</h2>
        <p className="mb-4">在這個實驗中，您將聆聽數段音樂片段，並分享您對這些音樂的經驗感受反饋。</p>
        <p className="mb-4">實驗總長度約 10-15 分鐘。為確保數據品質，請您盡可能在安靜的環境下專心聆聽並作答。</p>
        <p className="mb-6">您的回應將完全匿名，僅供研究目的使用。感謝您的時間！</p>

        <div className="space-y-4">
          <div className="form-group">
            <label className="block mb-2 font-medium">年齡:</label>
            <select 
              name="age" 
              value={formData.age} 
              onChange={handleFormChange}
              className="w-full p-2 border rounded-md"
            >
              <option value="">年齡區間</option>
              <option value="18-24">18-24</option>
              <option value="25-34">25-34</option>
              <option value="35-44">35-44</option>
              <option value="45-54">45-54</option>
              <option value="55-64">55-64</option>
              <option value="65+">65+</option>
            </select>
          </div>

          <div className="form-group">
            <label className="block mb-2 font-medium">性別:</label>
            <select 
              name="gender" 
              value={formData.gender} 
              onChange={handleFormChange}
              className="w-full p-2 border rounded-md"
            >
              <option value="">請選擇</option>
              <option value="male">男性</option>
              <option value="female">女性</option>
              <option value="others">其他</option>
            </select>
          </div>

          <div className="form-group">
            <label className="block mb-2 font-medium">音樂背景:</label>
            <select 
              name="musicalBackground" 
              value={formData.musicalBackground} 
              onChange={handleFormChange}
              className="w-full p-2 border rounded-md"
            >
              <option value="">請選擇您的程度</option>
              <option value="none">無音樂訓練</option>
              <option value="beginner">初學者 (少於兩年)</option>
              <option value="intermediate">中等 (2-5 年)</option>
              <option value="advanced">進階 (5+ 年)</option>
              <option value="professional">職業音樂人</option>
            </select>
          </div>

          <div className="form-group">
            <label className="block mb-2 font-medium">請問您會樂理嗎?</label>
            <select 
              name="theory" 
              value={formData.theory} 
              onChange={handleFormChange}
              className="w-full p-2 border rounded-md"
            >
              <option value="">請選擇</option>
              <option value="none">完全不會</option>
              <option value="basic">基本</option>
              <option value="intermediate">中等</option>
              <option value="advanced">進階</option>
            </select>
          </div>

          <div className="form-group">
            <label className="block mb-2 font-medium">請問您聽音樂的頻率:</label>
            <select 
              name="listenFrequency" 
              value={formData.listenFrequency} 
              onChange={handleFormChange}
              className="w-full p-2 border rounded-md"
            >
              <option value="">請選擇頻率</option>
              <option value="mostthetime">隨時</option>
              <option value="everyday">每天</option>
              <option value="everyweek">每週</option>
              <option value="sometimes">偶爾</option>
            </select>
          </div>

          <div className="form-group">
            <label className="block mb-2 font-medium">請問您參加現場演出的頻率:</label>
            <select 
              name="liveFrequency" 
              value={formData.liveFrequency} 
              onChange={handleFormChange}
              className="w-full p-2 border rounded-md"
            >
              <option value="">請選擇</option>
              <option value="never">從不</option>
              <option value="weekly">平均每週一次</option>
              <option value="monthly">平均每月一次</option>
              <option value="halfyear">平均每半年一次</option>
              <option value="yearly">平均每年一次</option>
            </select>
          </div>

          <div className="form-group">
            <label className="block mb-2 font-medium">當前聆聽設置:</label>
            <select 
              name="listeningEnvironment" 
              value={formData.listeningEnvironment} 
              onChange={handleFormChange}
              className="w-full p-2 border rounded-md"
            >
              <option value="">請選擇</option>
              <option value="headphones-indoor">室內：耳機</option>
              <option value="headphones-outdoor">室外：耳機</option>
              <option value="speakers">電腦/筆電/平板直接播放</option>
              <option value="other">其他</option>
            </select>
          </div>

          <div className="form-group">
            <label className="block mb-2 font-medium">參與者ID (選填):</label>
            <input 
              type="text" 
              name="participantId" 
              value={formData.participantId} 
              onChange={handleFormChange}
              placeholder="可留空，系統會自動生成"
              className="w-full p-2 border rounded-md"
            />
          </div>

          <button 
            onClick={startExperiment}
            className="w-full bg-blue-500 text-white py-3 px-6 rounded-md hover:bg-blue-600 transition-colors"
          >
            開始實驗
          </button>
        </div>
      </div>
    </div>
  );

  const renderLoadingScreen = () => (
    <div className="flex items-center justify-center min-h-screen">
      <div className="text-center">
        <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
        <p className="mt-4 text-lg">正在載入音樂檔案，請稍候...</p>
      </div>
    </div>
  );

  const renderExperimentScreen = () => (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-lg shadow-lg p-8">
        <div className="mb-6">
          <p className="text-lg mb-2">進度: {currentStimulusIndex + 1}/{totalStimuli}</p>
          <div className="w-full bg-gray-200 rounded-full h-4">
            <div 
              className="bg-blue-500 h-4 rounded-full transition-all duration-300"
              style={{ width: `${((currentStimulusIndex + 1) / totalStimuli) * 100}%` }}
            ></div>
          </div>
        </div>

        <h2 className="text-xl font-semibold mb-6">
          請您仔細聆聽這段音樂。您可以點擊「播放音樂」按鈕播放，若需重聽可再次點擊。
        </h2>

        <div className="mb-8">
          <button 
            onClick={playCurrentStimulus}
            disabled={isPlaying}
            className={`px-6 py-3 rounded-md font-medium ${
              isPlaying 
                ? 'bg-gray-400 cursor-not-allowed' 
                : 'bg-blue-500 hover:bg-blue-600 text-white'
            }`}
          >
            {isPlaying ? '播放中...' : '播放音樂'}
          </button>

          {playCountsPerStimulus[currentStimulusIndex] > 0 && (
            <span className="ml-4 text-gray-600">
              播放次數: {playCountsPerStimulus[currentStimulusIndex]}
            </span>
          )}

          {/* Audio visualization */}
          <div className="mt-4 bg-gray-100 h-32 rounded-lg relative overflow-hidden">
            <div 
              className="absolute bottom-0 left-0 h-2 bg-blue-500 transition-all duration-100"
              style={{ width: `${playProgress}%` }}
            ></div>
          </div>
        </div>

        {/* Questions */}
        <div className="space-y-8">
          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">請問您有多喜歡這段音樂？</h3>
            <p className="mb-4">請拖動滑桿來評估您對這段音樂的喜好程度。</p>
            <input 
              type="range" 
              min="1" 
              max="10" 
              value={responses.likeRating}
              onChange={(e) => handleResponseChange('likeRating', e.target.value)}
              className="w-full"
            />
            <div className="flex justify-between text-sm text-gray-600 mt-2">
              <span>1（非常不喜歡）</span>
              <span>5（普通）</span>
              <span>10（非常喜歡）</span>
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">對您而言，這段音樂的感知複雜度如何？</h3>
            <p className="mb-4">請評估這段音樂對您來說，聽起來是不是有點難理解。</p>
            <input 
              type="range" 
              min="1" 
              max="7" 
              value={responses.complexRating}
              onChange={(e) => handleResponseChange('complexRating', e.target.value)}
              className="w-full"
            />
            <div className="flex justify-between text-sm text-gray-600 mt-2">
              <span>1（非常簡單）</span>
              <span>4（普通）</span>
              <span>7（非常複雜）</span>
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">請問您對這類音樂有多熟悉？</h3>
            <p className="mb-4">請評估您過去接觸或聆聽這類音樂的頻率和程度。</p>
            <input 
              type="range" 
              min="1" 
              max="7" 
              value={responses.familiarRating}
              onChange={(e) => handleResponseChange('familiarRating', e.target.value)}
              className="w-full"
            />
            <div className="flex justify-between text-sm text-gray-600 mt-2">
              <span>1 (非常不熟悉)</span>
              <span>4 (好像有聽過)</span>
              <span>7 (非常熟悉)</span>
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">你對這段音樂的「結構」理解或欣賞程度為何？</h3>
            <p className="mb-4">請評估您是否能理解或欣賞這段音樂的組織方式、發展脈絡，以及各個段落的安排等。</p>
            <div className="space-y-2">
              {[
                { value: '1', label: '1 非常容易理解/欣賞 (結構非常清晰)' },
                { value: '2', label: '2 容易理解/欣賞' },
                { value: '3', label: '3 普通 (不特別理解，也不覺得困難)' },
                { value: '4', label: '4 較難理解/欣賞' },
                { value: '5', label: '5 非常難理解/欣賞 (結構非常混亂或不明)' }
              ].map(option => (
                <label key={option.value} className="flex items-center p-2 hover:bg-gray-100 rounded cursor-pointer">
                  <input 
                    type="radio" 
                    name="structure" 
                    value={option.value}
                    checked={responses.structureRating === option.value}
                    onChange={(e) => handleResponseChange('structureRating', e.target.value)}
                    className="mr-3"
                  />
                  <span>{option.label}</span>
                </label>
              ))}
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">這段音樂對你情緒的影響有多強烈？</h3>
            <p className="mb-4">請評估這段音樂在多大程度上觸動或改變了您的情緒狀態。</p>
            <div className="space-y-2">
              {[
                { value: '1', label: '1 完全無感 (沒有引起任何情緒波動)' },
                { value: '2', label: '2 輕微影響' },
                { value: '3', label: '3 中等影響 (略有感受，但不強烈)' },
                { value: '4', label: '4 較強影響' },
                { value: '5', label: '5 非常強烈 (引起非常明顯的情緒反應)' }
              ].map(option => (
                <label key={option.value} className="flex items-center p-2 hover:bg-gray-100 rounded cursor-pointer">
                  <input 
                    type="radio" 
                    name="emotion" 
                    value={option.value}
                    checked={responses.emotionRating === option.value}
                    onChange={(e) => handleResponseChange('emotionRating', e.target.value)}
                    className="mr-3"
                  />
                  <span>{option.label}</span>
                </label>
              ))}
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">請問您有多投入這段音樂？</h3>
            <p className="mb-4">請評估您在聆聽這段音樂時，注意力集中的程度，以及是否完全沉浸其中。</p>
            <input 
              type="range" 
              min="1" 
              max="7" 
              value={responses.engagementRating}
              onChange={(e) => handleResponseChange('engagementRating', e.target.value)}
              className="w-full"
            />
            <div className="flex justify-between text-sm text-gray-600 mt-2">
              <span>1 (非常抽離)</span>
              <span>4 (普通)</span>
              <span>7 (完全沉浸)</span>
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">在聆聽這段音樂時，您最主要注意到哪個音樂成分？</h3>
            <p className="mb-4">請選擇最吸引您注意力的音樂元素。</p>
            <div className="space-y-2">
              {[
                { value: 'melody', label: '旋律 (Melody)' },
                { value: 'rhythm', label: '節奏 (Rhythm)' },
                { value: 'harmony', label: '和聲 (Harmony)' },
                { value: 'timbre', label: '音色 (Timbre)' },
                { value: 'none', label: '毫無概念' }
              ].map(option => (
                <label key={option.value} className="flex items-center p-2 hover:bg-gray-100 rounded cursor-pointer">
                  <input 
                    type="radio" 
                    name="component" 
                    value={option.value}
                    checked={responses.selectedComponent === option.value}
                    onChange={(e) => handleResponseChange('selectedComponent', e.target.value)}
                    className="mr-3"
                  />
                  <span>{option.label}</span>
                </label>
              ))}
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold mb-4">您認為這首音樂屬於哪種曲風？</h3>
            <p className="mb-4">請選擇您認為最符合這段音樂的曲風分類。若不確定，可選擇「無法辨識」。</p>
            <select 
              value={responses.genreSelection}
              onChange={(e) => handleResponseChange('genreSelection', e.target.value)}
              className="w-full p-2 border rounded-md"
            >
              <option value="">請選擇曲風</option>
              <option value="classical">古典音樂 (Classical)</option>
              <option value="folk">民謠 (Folk)</option>
              <option value="citypop">城市流行 (City Pop)</option>
              <option value="pop_dance">流行/舞曲 (Pop/Dance)</option>
              <option value="pop">流行 (Pop)</option>
              <option value="techno">科技舞曲 (Techno)</option>
              <option value="math_rock">數字搖滾 (Math Rock)</option>
              <option value="jazz">爵士樂 (Jazz)</option>
              <option value="scandipop_tropical">北歐流行/熱帶風格 (Scandipop/Tropical)</option>
              <option value="other">其他</option>
              <option value="not_sure">無法辨識</option>
            </select>
          </div>

          <button 
            onClick={nextStimulus}
            disabled={!hasPlayedStimulus}
            className={`w-full py-3 px-6 rounded-md font-medium ${
              hasPlayedStimulus 
                ? 'bg-blue-500 hover:bg-blue-600 text-white' 
                : 'bg-gray-300 cursor-not-allowed text-gray-500'
            }`}
          >
            下一題
          </button>
        </div>
      </div>
    </div>
  );

  const renderCompletionScreen = () => (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-lg shadow-lg p-8 text-center">
        <h2 className="text-2xl font-bold mb-4">感謝您的參與！</h2>
        <p className="text-lg mb-6">您的回應已被記錄。</p>

        {experimentResults.experimentSummary.totalExperimentDurationMin && (
          <div className="bg-blue-50 p-6 rounded-lg mb-6">
            <h3 className="text-xl font-semibold mb-4">實驗摘要</h3>
            <p className="mb-2">
              總實驗時間: {experimentResults.experimentSummary.totalExperimentDurationMin} 分鐘
            </p>
            <p className="mb-2">
              總播放次數: {experimentResults.experimentSummary.playStatistics.totalPlays}
            </p>
            <p>
              平均每個音樂片段播放次數: {experimentResults.experimentSummary.playStatistics.averagePlaysPerStimulus} 次
            </p>
          </div>
        )}

        <div className="space-y-4">
          <button 
            onClick={downloadResults}
            className="bg-green-500 text-white py-3 px-6 rounded-md hover:bg-green-600 transition-colors"
          >
            下載實驗數據 (JSON備份)
          </button>

          <button 
            onClick={() => window.location.reload()}
            className="bg-gray-500 text-white py-3 px-6 rounded-md hover:bg-gray-600 transition-colors ml-4"
          >
            重新開始
          </button>
        </div>
      </div>
    </div>
  );

  // Main render
  return (
    <div className="min-h-screen bg-gray-100">
      {currentScreen === 'intro' && renderIntroScreen()}
      {currentScreen === 'loading' && renderLoadingScreen()}
      {currentScreen === 'experiment' && renderExperimentScreen()}
      {currentScreen === 'completion' && renderCompletionScreen()}

      <footer className="fixed bottom-0 left-0 right-0 bg-gray-100 p-4 text-center border-t">
        <p className="text-sm text-gray-600">
          有任何建議與問題歡迎聯繫: example@nthu.edu.tw
        </p>
      </footer>
    </div>
  );
};

export default MusicExperimentApp;
