// ===== Utility Functions =====
const generateParticipantId = () => {
  const timestamp = Date.now();
  const randomPart = Math.floor(Math.random() * 10000);
  return `P${timestamp}-${randomPart}`;
};
const shuffleArray = array => {
  const newArray = [...array];
  for (let i = newArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
  }
  return newArray;
};
const getStimulusFilePath = (stimulus, baseUrl = '') => {
  return `${baseUrl}/music/${stimulus.genre}/${stimulus.filename}`;
};
const selectMusic = () => {
  const selected = [];
  const genreDistribution = {
    "classical": 3,
    "math_rock": 3,
    "jazz": 3,
    "pop_dance": 1,
    "scandipop": 1,
    "jazz_pop": 1
  };
  Object.entries(genreDistribution).forEach(([genre, count]) => {
    if (count === 0) return;
    const genreSongs = [...new Set(musicFiles.filter(m => m.genre === genre).map(m => m.song_id))];
    const shuffledSongs = shuffleArray(genreSongs);
    if (count === 1) {
      const song = shuffledSongs[0];
      const segmentType = Math.random() < 0.5 ? "start" : "mid";
      const segment = musicFiles.find(m => m.genre === genre && m.song_id === song && m.segment_type === segmentType);
      if (segment) selected.push(segment);
    } else if (count === 3 && shuffledSongs.length >= 2) {
      const song1 = shuffledSongs[0];
      const startSegment = musicFiles.find(m => m.genre === genre && m.song_id === song1 && m.segment_type === "start");
      const midSegment = musicFiles.find(m => m.genre === genre && m.song_id === song1 && m.segment_type === "mid");
      const song2 = shuffledSongs[1];
      const randomSegmentType = Math.random() < 0.5 ? "start" : "mid";
      const randomSegment = musicFiles.find(m => m.genre === genre && m.song_id === song2 && m.segment_type === randomSegmentType);
      if (startSegment) selected.push(startSegment);
      if (midSegment) selected.push(midSegment);
      if (randomSegment) selected.push(randomSegment);
    }
  });
  console.log(`Selected ${selected.length} segments:`, selected.map(s => `${s.genre}-${s.song_id}-${s.segment_type}`));
  return shuffleArray(selected);
};

